mapper.select:
SELECT o.*,
rto.operator_id AS RTO__operator_id, rto.tenant_id AS RTO__tenant_id, rto.is_admin AS RTO__is_admin, rto.created_at AS RTO__created_at, rto.updated_at AS RTO__updated_at
FROM gsync_operator o LEFT JOIN gsync_r_tenant_operator rto ON o.operator_id = rto.operator_id WHERE o.operator_id = #{operatorId}

SELECT o.*,
rto.operator_id AS RTO__operator_id, rto.tenant_id AS RTO__tenant_id, rto.is_admin AS RTO__is_admin, rto.created_at AS RTO__created_at, rto.updated_at AS RTO__updated_at
FROM gsync_operator o LEFT JOIN gsync_r_tenant_operator rto ON o.operator_id = rto.operator_id WHERE rto.tenant_id = #{tenantId}
